import React from "react";
import styled, { keyframes } from "styled-components";

const Section = styled.section`
  min-height: 100vh;
  width: 100vw;
  background-color: ${(props) => props.theme.text};
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
`;

const move = keyframes`
0%{transform: translateX(100%)};
100%{ transform: translateX(-100%)}`;

const Row = styled.div`
  background-color: lightblue;
  white-space: nowrap;
  box-sizing: content-box;
  margin: 2rem 0;
  display: flex;

  animation: ${move} 20 s linear infinite ${(props) => props.direction};

  div {
    width: 5rem;
    height: 5rem;
    margin: 2rem;
    background-color: yellow;
  }
`;

const ImgContainer = styled.div`
  width: 15rem;
  margin: 0 auto;
  background-color: ${(props) => props.theme.body};

  border-radius: 20px;
  cursor: pointer;

  img {
    width: 100%;
    height: auto;
  }
`;
0;
const NftItem = ({ img, number, price }) => {
  return (
    <ImgContainer>
      <img src={img} alt="" />
    </ImgContainer>
  );
};

const Showcase = () => {
  return (
    <Section>
      <Row direction="none">
        <div>1</div>
        <div>1</div>
        <div>1</div>
        <div>1</div>
        <div>1</div>
      </Row>

      <Row direction="reverse">
        <div>1</div>
        <div>1</div>
        <div>1</div>
        <div>1</div>
        <div>1</div>
      </Row>
    </Section>
  );
};

export default Showcase;
